    'Vanilla Dream': 'data:image/jpeg;base64,',
    'Ocean Breeze': 'https://images.unsplash.com/photo-1550152341-3ab031ac7fb3?q=80&w=800&auto=format&fit=crop',
    'Lavender Fields': 'https://images.unsplash.com/photo-1595252129225-7da05083928a?q=80&w=800&auto=format&fit=crop',
    'Spice Route': 'https://images.unsplash.com/photo-1637777718753-0dee7c111087?q=80&w=800&auto=format&fit=crop',
    'Cedar Wood': 'https://images.unsplash.com/photo-1591375372156-542495912da9?q=80&w=800&auto=format&fit=crop',
    'Golden Amber': 'https://images.unsplash.com/photo-1615236051269-60a3ad64916d?q=80&w=800&auto=format&fit=crop',
    'Jasmine Nights': 'https://images.unsplash.com/photo-1545546384-7036e8f86bf1?q=80&w=800&auto=format&fit=crop',
    
    // Additional perfume images for even more variety
    'Mystic Moon': 'https://images.unsplash.com/photo-1616949755610-8c9bbc08f138?q=80&w=800&auto=format&fit=crop',
    'Desert Rose': 'https://images.unsplash.com/photo-1605217409100-3e5fe749e4a3?q=80&w=800&auto=format&fit=crop',
    'Royal Oud': 'https://images.unsplash.com/photo-1557602302-77ab7549acec?q=80&w=800&auto=format&fit=crop',
    'Black Orchid': 'https://images.unsplash.com/photo-1621640875110-5294e2ba3e9c?q=80&w=800&auto=format&fit=crop',
    'Summer Solstice': 'https://images.unsplash.com/photo-1602143407151-7111542de6e8?q=80&w=800&auto=format&fit=crop',
    'Amber Aura': 'https://images.unsplash.com/photo-1598104358204-9ae3932e2ff0?q=80&w=800&auto=format&fit=crop',
    'Divine Iris': 'https://images.unsplash.com/photo-1550617931-02093880ffbd?q=80&w=800&auto=format&fit=crop',
    'Sandalwood Mist': 'https://images.unsplash.com/photo-1563170352-33cd543758b7?q=80&w=800&auto=format&fit=crop',
    'Floral Euphoria': 'https://images.unsplash.com/photo-1557267538-a6d8c6c7e928?q=80&w=800&auto=format&fit=crop',
    'Amber Woods': 'https://images.unsplash.com/photo-1616949980300-77ba6d6a5278?q=80&w=800&auto=format&fit=crop',
    'Silver Musk': 'https://images.unsplash.com/photo-1547887538-bad3aeb4886d?q=80&w=800&auto=format&fit=crop',
    'Tropical Dream': 'https://images.unsplash.com/photo-1620960286805-7a89642d3c1c?q=80&w=800&auto=format&fit=crop',
